# -*- coding: utf-8 -*-

from odoo import models, fields, api, _
from odoo.exceptions import UserError, ValidationError, Warning

DEFAULT_CERTIFICATE = b''

class Company(models.Model):
    _inherit = 'res.company'
    
    @api.model
    def _default_digital_certificate(self):
        return DEFAULT_CERTIFICATE
    
    beta_service = fields.Boolean(string='¿Servidor de pruebas?', default=True)
    user_sol = fields.Char(string='Usuario SOL', default='MODDATOS')
    pass_sol = fields.Char(string='Contraseña SOL', default='MODDATOS')
    digital_certificate = fields.Binary(string='Certificado digital de la SUNAT', default=DEFAULT_CERTIFICATE)
    digital_certificate_filename = fields.Char(string='Nombre del archivo de certificado digital de la SUNAT', default='123456.p12')
    digital_password = fields.Char(string='Contraseña del certificado digital', default='123456')
    
    def _localization_use_documents(self) :
        """ This method is to be inherited by localizations and return True if localization use documents """
        self.ensure_one()
        if self.country_id == self.env.ref('base.pe', False) :
            return True
        else :
            return super(Company, self)._localization_use_documents()
